import React from "react";
import { 
  Facebook, 
  Instagram, 
  Youtube, 
  Linkedin, 
  Twitter,
  MessageCircle,
  Send
} from "lucide-react";

const StayConnected = () => {
  const socialPlatforms = [
    { name: "Telegram", icon: Send, color: "bg-blue-500", href: "#" },
    { name: "Messenger", icon: MessageCircle, color: "bg-blue-600", href: "#" },
    { name: "YouTube", icon: Youtube, color: "bg-red-600", href: "#" },
    { name: "Instagram", icon: Instagram, color: "bg-pink-500", href: "#" },
    { name: "WhatsApp", icon: MessageCircle, color: "bg-green-500", href: "#" },
    { name: "Facebook", icon: Facebook, color: "bg-blue-700", href: "#" },
    { name: "LinkedIn", icon: Linkedin, color: "bg-blue-800", href: "#" },
    { name: "TikTok", icon: Send, color: "bg-black", href: "#" },
    { name: "Twitter", icon: Twitter, color: "bg-blue-400", href: "#" }
  ];

  return (
    <section className="py-16 bg-gray-100">
      <div className="max-w-6xl mx-auto px-8 text-center">
        <h2 className="text-3xl font-bold text-gray-800 mb-4">Stay Connected</h2>
        <p className="text-gray-600 text-lg mb-12">
          Stay connected with us through your favorite social platforms.
        </p>

        <div className="flex flex-wrap justify-center gap-6">
          {socialPlatforms.map((platform, index) => {
            const IconComponent = platform.icon;
            return (
              <a
                key={index}
                href={platform.href}
                className={`${platform.color} text-white p-4 rounded-full hover:scale-110 transition-transform duration-300 shadow-lg hover:shadow-xl`}
                title={platform.name}
              >
                <IconComponent className="w-6 h-6" />
              </a>
            );
          })}
        </div>
      </div>
    </section>
  );
};

export default StayConnected;
