import React from "react";
import { ArrowRight } from "lucide-react";

const JoinCourses = () => {
  return (
    <section className="py-20 bg-gradient-to-b from-white via-gray-400 to-white">
      <div className="max-w-6xl mx-auto px-8 text-center">
        <h2 className="text-4xl font-bold text-gray-800 mb-8">
          Join Our Courses
        </h2>

        <p className="text-lg text-gray-700 mb-12 max-w-4xl mx-auto leading-relaxed">
          For over 20 years, British Way English Academy has been Sri Lanka's largest English training institute, 
          with 16 branches and over 21,000 graduates annually. Guided by a passionate, qualified team, 
          we believe every student has unlimited potential and we're here to unlock it.
        </p>

        <button className="px-8 py-4 bg-blue-600 text-white rounded-xl shadow-lg hover:bg-blue-700 transition-colors text-lg font-semibold flex items-center mx-auto">
          REGISTER NOW
          <ArrowRight className="ml-2 w-5 h-5" />
        </button>
      </div>
    </section>
  );
};

export default JoinCourses;
