import React from "react";

const Hero = () => {
  return (
    <section className="relative w-full h-[90vh] overflow-hidden">
      <video autoPlay loop muted playsInline className="absolute top-0 left-0 w-full h-full object-cover">
        <source src="/images/herovideo.mp4" type="video/mp4" />
      </video>
      <div className="absolute inset-0 bg-black/40" />
      <div className="absolute top-0 w-full flex justify-between items-center px-10 py-4 z-20">
        <img src="/images/logo.png" alt="British Way" className="h-12" />
        <ul className="flex space-x-8 text-white font-medium">
          <li className="hover:text-purple-400 cursor-pointer">Home</li>
          <li className="hover:text-purple-400 cursor-pointer">Courses</li>
          <li className="hover:text-purple-400 cursor-pointer">About Us</li>
          <li className="hover:text-purple-400 cursor-pointer">Contact Us</li>
          <li className="hover:text-purple-400 cursor-pointer">Branches</li>
        </ul>
      </div>
    </section>
  );
};

export default Hero;
