import React, { useState } from "react";
import { ChevronLeft, ChevronRight } from "lucide-react";

const programmes = [
  { title: "Two Months Diploma", subtitle: "Extended English Language Training in 2 Months", img: "/programmes/2months.jpg" },
  { title: "Online Diploma", subtitle: "Extended English Language Training in 2 Months", img: "/programmes/online.jpg" },
  { title: "Weekend Diploma", subtitle: "Extended English Language Training in 2 Months", img: "/programmes/weekend.jpg" },
  { title: "Kid Class", subtitle: "Extended English Language Training in 2 Months", img: "/programmes/kid.jpg" },
];

const Programmes = () => {
  const [index, setIndex] = useState(0);
  const next = () => setIndex((prev) => (prev + 1) % programmes.length);
  const prev = () => setIndex((prev) => (prev - 1 + programmes.length) % programmes.length);

  return (
    <section className="py-12 bg-gradient-to-b from-gray-50 to-gray-200 text-center">
      <h2 className="text-2xl font-bold mb-4">Our Programmes</h2>
      <p className="text-gray-500 mb-8">Working together to empower learning and growth.</p>
      <div className="relative w-full max-w-6xl mx-auto flex items-center">
        <button onClick={prev} className="absolute left-0 z-10 bg-white/80 hover:bg-white p-3 rounded-full shadow"><ChevronLeft size={24} /></button>
        <div className="w-full flex overflow-hidden">
          {programmes.slice(index, index + 3).map((p, i) => (
            <div key={i} className="flex-1 mx-3 rounded-2xl shadow-lg relative h-80 overflow-hidden group">
              <img src={p.img} alt={p.title} className="absolute inset-0 w-full h-full object-cover" />
              <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/40 to-transparent" />
              <div className="absolute bottom-6 left-6 text-left text-white">
                <h3 className="text-lg font-bold">{p.title}</h3>
                <p className="text-sm opacity-90 mb-3">{p.subtitle}</p>
                <button className="px-4 py-2 bg-purple-600 rounded-lg shadow hover:bg-purple-700">More Details</button>
              </div>
            </div>
          ))}
        </div>
        <button onClick={next} className="absolute right-0 z-10 bg-white/80 hover:bg-white p-3 rounded-full shadow"><ChevronRight size={24} /></button>
      </div>
    </section>
  );
};

export default Programmes;
